INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (24, 'Laravel', '/storage/image/nmPI8G2SOM8g3hFrLHhfrYDOB7zT1QA7HA3DRFjp.png', 'https://laravel.com', 'Laravel is a web application framework with expressive, elegant syntax. We’ve already laid the foundation — freeing you to create without sweating the small things.', 'expert', '2023-07-31 14:16:43', '2023-08-01 06:42:39');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (25, 'Vue.js', '/storage/image/YvxwPBOhpppSBidPSKv5mnYLAKIQqBv7SVT0aUMb.png', 'https://vuejs.org', 'An approachable, performant and versatile framework for building web user interfaces.', 'advanced', '2023-07-31 14:30:02', '2023-08-01 12:10:25');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (33, 'GitHub', '/storage/image/OvuIv1iTUDvttZ0oiHhLdaxEmkOgLQ8lSO9xQU8P.png', 'https://github.com', 'Whether you’re scaling your startup or just learning how to code, GitHub is your home. Join the world’s largest developer platform to build the innovations that empower humanity. Let’s build from here.', 'expert', '2023-08-01 11:24:52', '2023-08-01 11:24:52');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (34, 'PHP', '/storage/image/aXkX3uDMUJZ7J4qaIbW0Y0DwwwZX9le0AGvvVw6K.png', 'https://www.php.net', 'PHP (recursive acronym for PHP: Hypertext Preprocessor) is a widely-used open source general-purpose scripting language that is especially suited for web development and can be embedded into HTML.', 'expert', '2023-08-01 11:26:13', '2023-08-01 11:26:13');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (35, 'Inertia.js', '/storage/image/PHcAav6EH6uEpXGzJNQ8d2gTE2Xy6wUcwam2mLT4.png', 'https://inertiajs.com', 'Inertia allows you to create fully client-side rendered, single-page apps, without the complexity that comes with modern SPAs. It does this by leveraging existing server-side patterns that you already love.', 'intermediate', '2023-08-01 11:27:50', '2023-08-01 11:27:50');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (36, 'Alpine.js', '/storage/image/xQGW63nJLqI1cPTXrCG6pKBlEfl2QBrGdyJEKYAO.png', 'https://alpinejs.dev', 'Alpine is a rugged, minimal tool for composing behavior directly in your markup. Think of it like jQuery for the modern web. Plop in a script tag and get going.', 'advanced', '2023-08-01 11:29:31', '2023-08-01 11:29:31');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (37, 'JavaScript', '/storage/image/JNFikAZXI2CzHrljlq0PPPxiRv7KFe1tpy5RzqqP.png', 'https://developer.mozilla.org/en-US/docs/Web/JavaScript', 'JavaScript (JS) is a lightweight interpreted (or just-in-time compiled) programming language with first-class functions. While it is most well-known as the scripting language for Web pages, many non-browser environments also use it, such as Node.js, Apache CouchDB and Adobe Acrobat.', 'advanced', '2023-08-01 12:01:51', '2023-08-01 12:03:05');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (38, 'HTML', '/storage/image/W8tkiaRPGA9VDk4BUspRhGRS8U41uwBUebv7vyzF.png', 'https://developer.mozilla.org/en-US/docs/Web/HTML', 'HTML (HyperText Markup Language) is the most basic building block of the Web. It defines the meaning and structure of web content. Other technologies besides HTML are generally used to describe a web page\'s appearance/presentation (CSS) or functionality/behavior (JavaScript).', 'expert', '2023-08-01 12:02:55', '2023-08-01 12:02:55');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (39, 'CSS', '/storage/image/ItPGCQeONrm6KkbjWq9Uopsne6RSY548NjUCmEU9.png', 'https://developer.mozilla.org/en-US/docs/Web/CSS', 'Cascading Style Sheets (CSS) is a stylesheet language used to describe the presentation of a document written in HTML or XML (including XML dialects such as SVG, MathML or XHTML). CSS describes how elements should be rendered on screen, on paper, in speech, or on other media.', 'advanced', '2023-08-01 12:03:54', '2023-08-01 12:03:54');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (40, 'Tailwindcss', '/storage/image/Cs676xN1um2Gea1KAyb342ITWSdcHqyJcLCfGU4w.png', 'https://tailwindcss.com/', 'Utility classes help you work within the constraints of a system instead of littering your stylesheets with arbitrary values. They make it easy to be consistent with color choices, spacing, typography, shadows, and everything else that makes up a well-engineered design system.', 'expert', '2023-08-01 12:06:22', '2023-08-01 12:06:22');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (41, 'Git', '/storage/image/bZluniDZSoR6ZcQUhas1Pf4LfowXaDJot6zX4eeO.png', 'https://git-scm.com', 'Git is a free and open source distributed version control system designed to handle everything from small to very large projects with speed and efficiency.', 'advanced', '2023-08-01 12:10:01', '2023-08-01 12:10:01');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (42, 'PWA', '/storage/image/CIOfDCDrNvVY87ygYn9YJY2KwbO1UDXCkMKlIxIf.png', 'https://developer.mozilla.org/en-US/docs/Web/Progressive_web_apps', 'A progressive web app (PWA) is an app that\'s built using web platform technologies, but that provides a user experience like that of a platform-specific app.', 'expert', '2023-08-01 12:11:33', '2023-08-01 12:11:33');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (43, 'WordPress', '/storage/image/xQAnklqGN0RKTg14TWOZInd5xyNDkN6nyJE64GoN.png', 'https://wordpress.com', 'WordPress is a web content management system. It was originally created as a tool to publish blogs but has evolved to support publishing other web content, including more traditional websites, mailing lists and Internet forum, media galleries, membership sites, learning management systems and online stores.', 'advanced', '2023-08-01 12:14:04', '2023-08-01 12:14:04');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (44, 'Vite.js', '/storage/image/DpoagsLeudHNXvwM8vi7k2QNGKhFdMyA5LP96UsN.png', 'https://vitejs.dev', 'Vite (French word for "quick", pronounced /vit/, like "veet") is a new breed of frontend build tooling that significantly improves the frontend development experience. It consists of two major parts:\r\nA dev server that serves your source files over native ES modules, with rich built-in features and astonishingly fast Hot Module Replacement (HMR).\r\nA build command that bundles your code with Rollup, pre-configured to output highly optimized static assets for production.', 'advanced', '2023-08-01 12:28:00', '2023-08-01 12:28:00');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (45, 'Node.js', '/storage/image/7QQ3VHeofVnt7W6w8WsPh6rPmd7dtxyPkXAlDep7.png', 'https://nodejs.org/', 'Node.js is a cross-platform, open-source server environment that can run on Windows, Linux, Unix, macOS, and more. Node.js is a back-end JavaScript runtime environment, runs on the V8 JavaScript Engine, and executes JavaScript code outside a web browser.', 'advanced', '2023-08-01 12:29:57', '2023-08-01 12:29:57');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (46, 'Livewire', '/storage/image/b4gb1h7Z8z27dj1ye0DgIunrDz8nJaLJEQOfpLaE.svg', 'https://laravel-livewire.com/', 'Livewire is a full-stack framework for Laravel that makes building dynamic interfaces simple, without leaving the comfort of Laravel.', 'expert', '2023-08-01 12:32:45', '2023-08-01 12:32:45');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (47, 'MySQL', '/storage/image/xvhpH6DVTwNizRqdm9BtKW3hJ1TK1EL0w0uvKi3X.png', 'https://dev.mysql.com/', 'MySQL is an open-source relational database management system (RDBMS). Its name is a combination of "My", the name of co-founder Michael Widenius\'s daughter My, and "SQL", the acronym for Structured Query Language.', 'expert', '2023-08-01 12:36:46', '2023-08-01 12:36:46');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (48, 'MariaDB', '/storage/image/1UqGCfIWdZ7AsRilmCY4VSLeGAizIgs2vvd4fN5v.png', 'https://mariadb.org/', 'MariaDB Server is one of the most popular open source relational databases. It’s made by the original developers of MySQL and guaranteed to stay open source. It is part of most cloud offerings and the default in most Linux distributions.', 'expert', '2023-08-01 12:37:31', '2023-08-01 12:37:31');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (49, 'PostgreSQL', '/storage/image/zvLLIaizswQGUaVfejJOAwHogl9wtqx5BVRUvMVb.png', 'https://www.postgresql.org/', 'PostgreSQL is a powerful, open source object-relational database system with over 35 years of active development that has earned it a strong reputation for reliability, feature robustness, and performance.', 'advanced', '2023-08-01 12:38:21', '2023-08-01 12:38:21');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (50, 'SEO', '/storage/image/h0fHZo1onR20piLr2shM8zHD9tV8xMydmjRiJ3bD.png', NULL, 'Search engine optimization (SEO) is the process of improving the quality and quantity of website traffic to a website or a web page from search engines. SEO targets unpaid traffic (known as "natural" or "organic" results) rather than direct traffic or paid traffic. Unpaid traffic may originate from different kinds of searches, including image search, video search, academic search, news search, and industry-specific vertical search engines.', 'intermediate', '2023-08-01 12:42:24', '2023-08-01 12:42:24');
INSERT INTO `skills` (`id`, `title`, `img`, `url`, `description`, `level`, `created_at`, `updated_at`) VALUES (51, 'Responsive Design', '/storage/image/ZmAXnPBa1EiOrur3vBbF5Cz6en3EyijnQHDJ9ykH.png', NULL, 'Responsive web design (RWD) or responsive design is an approach to web design that aims to make web pages render well on a variety of devices and window or screen sizes from minimum to maximum display size to ensure usability and satisfaction.', 'expert', '2023-08-01 12:47:01', '2023-08-01 12:47:01');
